import React, {useEffect, useState} from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");

  const [lon, setLon] = useState('')
  const [lat, setLat] = useState('')
  const [address, setAddress] = useState('')


  const token = `pk.eyJ1IjoiaHV5dGhhaXNsbmEiLCJhIjoiY2s2cnBkYnJ4MDhpZDNucGtkcWd1NjVnbCJ9.Sm5nLs50orhq04iKl76a1Q`
  const url = `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(location)}.json?access_token=${token}`
  const searchLocation = (event) => {
    if (event.key === "Enter") {
      axios.get(url).then((response) => {
        setLon(response.data.features[0].center[0])
        setLat(response.data.features[0].center[1])
        setAddress(response.data.features[0].text)
      });
      setLocation("");
    }
  };

  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={(event) => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder="Enter City"
          type="text"
        />
      </div>
      <br/>
      <br/>
      {address}
      {lon}
      <br/>
      {lat}
    </div>
  );
}

export default App;
